tion updateDetailedCalendar($id, $st, $et, $sub, $ade, $dscr, $loc, $staffkey, $color, $tz){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "update `Staff Event Dimension` set"      . " `starttime`='" . php2MySqlTime(js2PhpTime($st)) . "', "      . " `End Time`='" . php2MySqlTime(js2PhpTime($et)) . "', "      . " `subject`='" . mysql_real_escape_string($sub) . "', "      . " `Is All Day Event`='" . mysql_real_escape_string($ade) . "', "      . " `description`='" . mysql_real_escape_string($dscr) . "', "      . " `location`='" . mysql_real_escape_string($loc) . "', "      . " `Staff Key`='" . mysql_real_escape_string($staffkey) . "', "      . " `color`='" . mysql_real_escape_string($color) . "' "      . "where `Staff Event Key`=" . $id;    //echo $sql;		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function removeCalendar($id){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "delete from `Staff Event Dimension` where `Staff Event Key`=" . $id;		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}header('Content-type:text/javascript;charset=UTF-8');$method = $_GET["method"];switch ($method) {    case "add":	//echo $_POST["CalendarStartTime"];       $ret = addCalendar($_POST["CalendarStartTime"], $_POST["CalendarEndTime"], $_POST["CalendarTitle"], $_POST["IsAllDayEvent"]);        break;    case "list":	$staff_id=$_REQUEST['staff_id'];        $ret = listCalendar($_POST["showda